import React, { Component } from "react";
import Navigation from "./Navigation";
import AddProduct from "./AddProduct";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../actions/authActions";
import ProductList from "./ProductList";
import { getItems } from "../actions/itemActions";
import {
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBContainer
} from "mdbreact";
class Shop extends Component {
  onLogout = () => {
    logout();
  };

  componentDidMount() {
    this.props.getItems();
  }

  render() {
    const { items } = this.props.item;

    const filteredItems = items.filter(item => item.userRefID === "");
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    console.log(filteredItems);

    return (
      <div className="w-100">
        <Navigation />
        <div className="container-fluid">
          <h1 className="text-center pt-4">Shop</h1>
        </div>

        <MDBContainer>
          <MDBCarousel
            activeItem={0}
            length={3}
            showControls={true}
            showIndicators={true}
            className="z-depth-1
                w-75 mx-auto"
            slide
          >
            {filteredItems.map((item, i) => (
              <MDBCarouselInner key={item._id}>
                <MDBCarouselItem itemId={i}>
                  <MDBView>
                    <img
                      className="d-block w-100"
                      src={item.productImage}
                      alt={item._id}
                    />
                    <MDBMask overlay="black-light" />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className="h3-responsive">{item.name}</h3>
                    <p>{item.description}</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>
              </MDBCarouselInner>
            ))}
          </MDBCarousel>
        </MDBContainer>

        {isAuthenticated === true ? (
          <div className="p-4 text-center">
            <div>
              <h3>Welcome {user.name}</h3>
              {/* <p>your id: {user._id}</p> */}
            </div>
            <ProductList />
            <hr />
            <AddProduct className="pt-5" />

            <button className="p-3 w-50 pt-4" onClick={this.onLogout}>
              logout here
            </button>
          </div>
        ) : (
          <div className="row m-3 text-center">
            <Link className="pb-3" to="/login">
              Click here to login!
            </Link>
            <Link to="/register">No account? register here</Link>
          </div>
        )}
      </div>
    );
  }
}
const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});
export default connect(mapStateToProps, { logout, getItems })(Shop);
