import React, { Component } from "react";
import Navigation from "./Navigation";
import AddProduct from "./AddProduct";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../actions/authActions";
import ProductList from "./ProductList";
import { getItems } from "../actions/itemActions";

class Shop extends Component {
  onLogout = () => {
    logout();
  };

  componentDidMount() {
    this.props.getItems();
  }

  render() {
    const { items } = this.props.item;

    const filteredItems = items.filter(item => item.userRefID === "");
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    console.log(filteredItems.length);

    return (
      <div className="w-100">
        <Navigation />
        <div className="container-fluid">
          <h1 className="text-center pt-4">Shop</h1>
          <div className="row">
            {filteredItems &&
              filteredItems.map((item, i) =>
                item.userRefID ? null : (
                  <div className="" key={i}>
                    <div className="">
                      <img className="" src={item.productImage} alt="" />
                    </div>
                    <div className="">
                      <h5>Name: {item.name}</h5>
                      {/* <p>Description: {item.description}</p> */}
                      <p>Category: {item.category}</p>
                      <p>Price: ${item.price}</p>
                    </div>

                    <div className="">
                      {/* <button onClick={() => this.onAddToCart(item, user)}>
                      add to cart
                    </button> */}
                      {/* <button onClick={() => this.onDeleteItem(item._id)}>
                      delete{" "}
                    </button> */}
                    </div>
                  </div>
                )
              )}
          </div>
        </div>
        <div></div>
        {isAuthenticated === true ? (
          <div className="p-4 text-center">
            <div>
              <h3>Welcome {user.name}</h3>
              {/* <p>your id: {user._id}</p> */}
            </div>
            <ProductList />
            <hr />
            <AddProduct className="pt-5" />

            <button className="p-3 w-50 pt-4" onClick={this.onLogout}>
              logout here
            </button>
          </div>
        ) : (
          <div className="row m-3 text-center">
            <Link className="pb-3" to="/login">
              Click here to login!
            </Link>
            <Link to="/register">No account? register here</Link>
          </div>
        )}
      </div>
    );
  }
}
const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});
export default connect(mapStateToProps, { logout, getItems })(Shop);
