import React, { Component } from "react";
import { connect } from "react-redux";
import {
  deleteItem,
  addItemToCart,
  getItems,
  addItem
} from "../actions/itemActions";
import { Button } from "bootstrap-4-react";

import CartModal from "./CartModal";
class ProductList extends Component {
  onDeleteItem = id => {
    this.props.deleteItem(id);
    console.log(id);
  };
  componentDidMount() {
    this.props.getItems();
  }

  onAddToCart = (item, user) => {
    const newItem = {
      name: item.name,
      description: item.description,
      category: item.category,
      price: item.price,
      userRefID: user._id,
      _id: item._id,
      productImage: item.productImage
    };
    this.props.addItemToCart(newItem);
  };

  render() {
    const { items } = this.props.item;
    const { user } = this.props.auth;

    const filteredItems = items.filter(item => item.userRefID === "");
    // const cartFilteredItems = items.filter(
    //   item => item.userRefID !== "" && item.userRefID === user._id
    // );

    // let cartTotal = cartFilteredItems.reduce(
    //   (prev, current) => prev + current.price,
    //   0
    // );
    // let cartTotalTax = cartTotal * 0.15;
    // let cartTotalWithTax = cartTotal && cartTotal + cartTotalTax;
    // console.log(cartTotalTax);
    // console.log(cartTotalWithTax);

    return (
      <div className="container-fluid">
        {/* Button trigger cart modal */}
        <Button
          className="p-2 m-2 w-25"
          primary
          outline
          data-toggle="modal"
          data-target="#exampleModal"
        >
          View my cart
        </Button>
        <h4>Product List</h4>
        <h5>Qty of products: {filteredItems.length}</h5>
        <h5 className=" m-4 float-left">Featured </h5>
        <div className="border-bottom border-lg "></div>

        <div className="row my-4 p-3 mx-auto justify-content-center">
          {filteredItems &&
            filteredItems.map((item, i) =>
              item.userRefID ? null : (
                <div
                  className="card col-sm-4 col-md-3 col-lg-2 m-2 p-3 "
                  key={i}
                >
                  <div className="">
                    <img className="p-3" src={item.productImage} alt="" />
                  </div>
                  <div className="text-center pt-2">
                    <h6 className="mx-auto pb-2">{item.name}</h6>
                    {/* <p className="font-weight-light">
                        Description: {item.description}
                      </p> */}
                    {/* <p>Category: {item.category}</p> */}
                    <p className="mx-auto">${item.price}</p>
                  </div>

                  <div className="text-center p-3">
                    <button
                      type="button"
                      className="btn btn-primary p-2 w-75"
                      onClick={() => this.onAddToCart(item, user)}
                    >
                      Add to cart
                    </button>
                    {/* <button onClick={() => this.onDeleteItem(item._id)}>
                        Delete{" "}
                      </button> */}
                  </div>
                </div>
              )
            )}
        </div>

        <CartModal />
      </div>
    );
  }
}
const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});
export default connect(mapStateToProps, {
  deleteItem,
  addItemToCart,
  addItem,
  getItems
})(ProductList);
