import React, { Component } from "react";

import { connect } from "react-redux";
import {
  deleteItem,
  addItemToCart,
  getItems,
  addItem,
  addItemToGuestCart,
  getGuestCartItems
} from "../actions/itemActions";

import shortid from "shortid";

class ProductList extends Component {
  componentDidMount() {
    this.props.getGuestCartItems();
  }

  onDeleteItem = id => {
    this.props.deleteItem(id);
  };

  onAddToGuestCart = item => {
    item.guestItemID =
      shortid.generate() + String(Math.floor(Math.random() * 30));

    this.props.addItemToGuestCart(item);
    this.props.getGuestCartItems();
  };

  onAddToCart = (item, user) => {
    const newItem = {
      name: item.name,
      description: item.description,
      category: item.category,
      price: item.price,
      userRefID: user._id,

      productImage: item.productImage
    };

    this.props.addItemToCart(newItem);
  };

  render() {
    const { items } = this.props.item;
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    const filteredItems = items.filter(item => item.userRefID === "");
    return (
      <div>
        <h2 className=" mt-4 mb-2 text-center">
          Shop : {filteredItems.length} Products{" "}
        </h2>
        <h5 className=" mx-4 my-2 float-left">Featured </h5>

        <div className="border-top border-lg "></div>

        <div className="row my-4 p-1 mx-auto justify-content-center container-fluid">
          {filteredItems &&
            filteredItems.map((item, i) =>
              item.userRefID ? null : (
                <div
                  className="card col-3 col-sm-3 col-md-2 col-lg-2 m-2 p-1 d-flex flex-column justify-content-between h-25 "
                  key={i}
                >
                  <div className="">
                    <img
                      className="p-3 img-fluid w-75 mx-auto d-block"
                      src={item.productImage}
                      alt=""
                    />
                  </div>
                  <div className="text-center pt-2">
                    <h6 className="mx-auto pb-2 pt-4">{item.name}</h6>
                    {/* <p>{item._id}</p> */}
                    {/* <p className="font-weight-light">
                        Description: {item.description}
                      </p> */}
                    {/* <p>Category: {item.category}</p> */}
                    <p className="mx-auto pb-4">${item.price}</p>
                  </div>

                  <div className="text-center p-3 ">
                    {isAuthenticated === true ? (
                      <button
                        type="button"
                        className="btn btn-outline-primary p-2 w-75 align-self-end"
                        onClick={() => this.onAddToCart(item, user)}
                      >
                        Add
                      </button>
                    ) : (
                      <button
                        type="button"
                        className="btn btn-outline-info p-2 w-75 align-self-end"
                        onClick={() => this.onAddToGuestCart(item)}
                      >
                        Add
                      </button>
                    )}
                    {/* <button onClick={() => this.onDeleteItem(item._id)}>
                      Delete{" "}
                    </button> */}
                  </div>
                </div>
              )
            )}
        </div>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});
export default connect(mapStateToProps, {
  deleteItem,
  addItemToCart,
  addItem,
  getItems,
  addItemToGuestCart,
  getGuestCartItems
})(ProductList);
