import React, { Component } from "react";
import { connect } from "react-redux";
import {
  deleteItem,
  addItemToCart,
  getItems,
  addItem,
  getGuestCartItems,
  clearGuestCart,
  deleteGuestItem,
  clearUserCart
  // loadLocalToUser,
} from "../actions/itemActions";
import { Modal, Button } from "bootstrap-4-react";

import { withRouter } from "react-router-dom";
class CartModal extends Component {
  componentDidMount() {
    this.props.getItems();
    this.props.getGuestCartItems();

    // this.props.loadLocalToUser();
  }

  onClearUserCart = (user, items) => {
    this.props.clearUserCart(user, items);
    // this.props.getItems();
  };

  onClearGuestCart = () => {
    this.props.clearGuestCart();
  };
  deleteGuestCartItem = item => {
    console.log(item.guestItemID);

    this.props.deleteGuestItem(item);
    this.props.getGuestCartItems();
  };

  // checkLocalAndDB = () => {
  //   const { isAuthenticated } = this.props.auth;
  //   const { user } = this.props.auth;

  //   if (isAuthenticated) {
  //     let existingGuestCart = JSON.parse(localStorage.getItem("guestCart"));

  //     const newItems = existingGuestCart.map(item => {
  //       item.userRefID === user._id ? item : item.userRefID === user._id;
  //     });
  //     console.log(newItems);
  //   }
  // };
  render() {
    const { items } = this.props.item;
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    const { guestCart } = this.props.item;

    // addGuestToUser = () => {

    const onGuestCartToUser = () => {
      if (isAuthenticated) {
        let existingGuestCart = JSON.parse(localStorage.getItem("guestCart"));

        // added userID to guest cart items
        let newItems =
          existingGuestCart &&
          existingGuestCart.filter(item => {
            if (item.userRefID !== user._id) {
              item.userRefID = user._id;
            }
            return item;
          });
        // console.log(newItems.map(item => item.userRefID));
        // console.log(newItems);
        // console.log(`User ${user.name}'s ID is ${user._id}`);

        // compare user items with guest items, return unique items not existing in user already
        let userLocalCompare =
          newItems &&
          newItems.filter(guestItem => {
            return items.some(
              userItem =>
                userItem._id === guestItem._id &&
                guestItem.guestItemID !== userItem.guestItemID
            );
          });

        console.log(userLocalCompare);
        this.props.addItemToCart(userLocalCompare);
      }
    };

    this.componentDidMount = () => {
      onGuestCartToUser();
    };

    // this.componentDidUpdate = () => {
    //   onGuestCartToUser();
    // };

    // };

    const deleteCartItem = item => {
      this.props.getItems();
      this.props.deleteItem(item._id);
      console.log(item);
    };

    let cartFilteredItems = isAuthenticated
      ? items.filter(
          item => item.userRefID !== "" && item.userRefID === user._id
        )
      : guestCart;

    let cartTotal =
      cartFilteredItems &&
      cartFilteredItems.reduce((prev, current) => prev + current.price, 0);
    let shippingCost = cartTotal && cartTotal > 100 ? 0 : 7.99;
    let cartTotalTax = cartTotal && cartTotal * 0.15;
    let cartTotalWithTax = cartTotal && cartTotal + cartTotalTax + shippingCost;
    // console.log(cartTotalTax);
    // console.log(cartTotalWithTax);
    const loggedCheckout = () => {
      this.props.history.push({
        pathname: "/shop/payment",
        state: cartTotalWithTax
      });
    };
    // console.log(existingGuestCart);
    const guestCheckout = () => {
      this.props.history.push({
        pathname: "/register"
      });
    };
    return (
      <div>
        <Modal id="exampleModal" fade>
          <Modal.Dialog centered className="m-auto">
            <Modal.Content>
              <Modal.Header className="p-3">
                <Modal.Title
                  style={{ textTransform: "capitalize" }}
                  className="float-left w-25 font-wieght-bolder"
                >
                  {isAuthenticated ? `${user.name}'s Cart` : "Guest Cart"}
                </Modal.Title>
                <Modal.Close className="float-right w-25">
                  <span aria-hidden="true" className="float-right">
                    &times;
                  </span>
                </Modal.Close>
              </Modal.Header>

              <div className="p-3 w-75 mx-auto">
                <h2>Items</h2>
                <h4>
                  Qty in cart:{" "}
                  {cartFilteredItems && cartFilteredItems.length === null
                    ? 0
                    : cartFilteredItems && cartFilteredItems.length}
                </h4>
                {/* {items &&
                  items */}

                {cartFilteredItems &&
                  cartFilteredItems.map((item, i) => (
                    // item.userRefID === user._id ?
                    <div className="card p-4" key={i}>
                      <p>{i}</p>
                      <h5>Name: {item.name}</h5>
                      <p>Description: {item.description}</p>
                      <p>Category: {item.category}</p>
                      <p>Price: ${item.price}</p>

                      <div className="image-container mx-auto">
                        <img
                          className="img-fluid img-thumbnail center-block"
                          src={item.productImage}
                          alt=""
                        />
                      </div>

                      <div className="row d-flex justify-content-center align-content-end pt-5 h-25">
                        <button
                          className="btn btn-primary w-50 p-2"
                          onClick={
                            isAuthenticated
                              ? () => deleteCartItem(item)
                              : () => this.deleteGuestCartItem(item)
                          }
                        >
                          Remove
                        </button>

                        <div className="m-3"></div>
                      </div>
                    </div>
                  ))}
              </div>

              <Button
                className="col-12 my-1 w-25 p-2"
                secondary
                onClick={
                  isAuthenticated
                    ? () => this.onClearUserCart(user, items)
                    : () => this.onClearGuestCart()
                }
              >
                Clear Cart
              </Button>

              {/* {guestCart && guestCart.map(item => <div>{item.name}</div>)} */}
              <Modal.Footer className="border-dark row px-5 py-3 ">
                <h5>Cart Total: {cartTotal && cartTotal.toFixed(2)}</h5>
                <h5>
                  Shipping & Handling:{" "}
                  {shippingCost === 0 || cartTotal === 0
                    ? "FREE"
                    : shippingCost}
                </h5>
                <h5>Tax: {cartTotalTax && cartTotalTax.toFixed(2)}</h5>
                <h3 className="font-weight-bolder border-top">
                  Order total ({cartFilteredItems && cartFilteredItems.length}{" "}
                  item): ${cartTotalWithTax && cartTotalWithTax.toFixed(2)}
                </h3>
                <Button
                  className="col-12 my-1 w-25 p-2"
                  secondary
                  data-dismiss="modal"
                >
                  Close cart
                </Button>
                <Button
                  data-dismiss="modal"
                  className="btn btn-primary btn-danger col-12 my-1 w-25 p-2"
                  // to={{
                  //   pathname: "/shop/payment",
                  //   state: cartTotalWithTax
                  // }}
                  // replace
                  onClick={
                    isAuthenticated
                      ? () => loggedCheckout(cartTotalWithTax)
                      : () => guestCheckout(cartTotalWithTax)
                  }
                >
                  CHECKOUT
                </Button>
              </Modal.Footer>
            </Modal.Content>
          </Modal.Dialog>
        </Modal>
      </div>
    );
  }
}
const mapStateToProps = state => ({
  item: state.item,
  auth: state.auth
});
export default connect(mapStateToProps, {
  deleteItem,
  addItemToCart,
  addItem,
  getItems,
  getGuestCartItems,
  clearGuestCart,
  deleteGuestItem,
  clearUserCart
  // loadLocalToUser,
})(withRouter(CartModal));
