import React, { Component } from "react";
import Navigation from "./Navigation";
import AddProduct from "./AddProduct";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { logout } from "../actions/authActions";
import ProductList from "./ProductList";
import { getItems } from "../actions/itemActions";
import {
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBContainer
} from "mdbreact";
class Shop extends Component {
  onLogout = () => {
    logout();
  };

  componentDidMount() {
    this.props.getItems();
  }

  render() {
    const { items } = this.props.item;

    const filteredItems = items.filter(item => item.userRefID === "");
    const { user } = this.props.auth;
    const { isAuthenticated } = this.props.auth;
    return (
      <div className="w-100">
        <Navigation />
        <div className="container-fluid">
          <h1 className="text-center pt-4">Shop</h1>
        </div>

        <MDBContainer>
          <MDBCarousel
            activeItem={1}
            length={3}
            showControls={true}
            showIndicators={true}
            className="z-depth-1"
          >
            <MDBCarouselInner>
              <MDBCarouselItem itemId="1">
                <MDBView>
                  <img
                    className="d-block w-100"
                    src="https://mdbootstrap.com/img/Photos/Slides/img%20(68).jpg"
                    alt="First slide"
                  />
                  <MDBMask overlay="black-light" />
                </MDBView>
                <MDBCarouselCaption>
                  <h3 className="h3-responsive">Light mask</h3>
                  <p>First text</p>
                </MDBCarouselCaption>
              </MDBCarouselItem>
              <MDBCarouselItem itemId="2">
                <MDBView>
                  <img
                    className="d-block w-100"
                    src="https://mdbootstrap.com/img/Photos/Slides/img%20(6).jpg"
                    alt="Second slide"
                  />
                  <MDBMask overlay="black-strong" />
                </MDBView>
                <MDBCarouselCaption>
                  <h3 className="h3-responsive">Strong mask</h3>
                  <p>Second text</p>
                </MDBCarouselCaption>
              </MDBCarouselItem>
              <MDBCarouselItem itemId="3">
                <MDBView>
                  <img
                    className="d-block w-100"
                    src="https://mdbootstrap.com/img/Photos/Slides/img%20(9).jpg"
                    alt="Third slide"
                  />
                  <MDBMask overlay="black-slight" />
                </MDBView>
                <MDBCarouselCaption>
                  <h3 className="h3-responsive">Slight Mast</h3>
                  <p>Third text</p>
                </MDBCarouselCaption>
              </MDBCarouselItem>
            </MDBCarouselInner>
          </MDBCarousel>
        </MDBContainer>

        {/*
            {filteredItems.map((item, i) => (

            ))} */}

        {isAuthenticated === true ? (
          <div className="p-4 text-center">
            <div>
              <h3>Welcome {user.name}</h3>
              {/* <p>your id: {user._id}</p> */}
            </div>
            <ProductList />
            <hr />
            <AddProduct className="pt-5" />

            <button className="p-3 w-50 pt-4" onClick={this.onLogout}>
              logout here
            </button>
          </div>
        ) : (
          <div className="row m-3 text-center">
            <Link className="pb-3" to="/login">
              Click here to login!
            </Link>
            <Link to="/register">No account? register here</Link>
          </div>
        )}
      </div>
    );
  }
}
const mapStateToProps = state => ({
  items: state.item.items,
  item: state.item,
  auth: state.auth,
  user: state.auth.user,
  isAuthenticated: state.auth.isAuthenticated
});
export default connect(mapStateToProps, { logout, getItems })(Shop);
